import './profile.css'
import React, { useState, useEffect } from "react";
import {useAuthValue} from './AuthContext'
import { signOut } from 'firebase/auth' 
import {firebase, auth } from './firebase'
import Navbar from "./components/Navbar";
import NoteAdd from "./components/NoteAdd";
import Notebook from "./components/Notebook";
import { async } from '@firebase/util';



function Profile() {
  const [noteBookData, setNoteBookData] = useState([]);
  const {currentUser} = useAuthValue()
  const updateNotes = () => {
    firebase
      .database()
      .ref("notebook")
      .on("child_added", (snapshot) => {
        let results = noteBookData;
        firebase.database().ref('notebook').orderByKey().once('value').then(function(snapshot)
        {
          snapshot.forEach(function(child) {
            let note = {
              email: child.val().email,
              public: child.val().public,
              id: child.key,
              title: child.val().title,
              description: child.val().description,
            };
            results.push(note);
           })

        }) 
        setNoteBookData([...results]);
      });

    firebase
      .database()
      .ref("notebook")
      .on("child_removed", (snapshot) => {
        let notebook = noteBookData;
        notebook = noteBookData.filter((note) => note.id !== snapshot.key);
        setNoteBookData([...notebook]);
      });
  };

  useEffect(() => {
    updateNotes();
  }, []);

  return (
    <div className="app">
      <Navbar />
      <div className="note-section">
        <NoteAdd />
        <Notebook notebook={noteBookData} />
      </div>
      <span onClick={() => signOut(auth)}>Sign Out</span>
    </div>
  );
}

export default Profile

