// components/Notebook/NoteAdd.js
import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/database";
import "./Notebook.css";


const Notebook = (props) => {
  const handleChange = (note) => {
    firebase.database().ref("notebook/public/"+note.id).update({title: "note.title"})
  };
  const deleteNotebook = (id) => {
    firebase.database().ref("notebook").child(id).remove();
  };

  return (
    <>
      <section className="notebook-container">
        <div className="notebook">
          {props.notebook.map((note, index) => (
            <React.Fragment key={index}>
              <div className={`${note.public ? "notebookInfo-public" : "notebookInfo"}`} key={note.id}>
                <div className="notebookInfo-title">
                  <h3>{note.title}</h3>
                  <h3>{note.email}</h3>
                  <h3>{note.public}</h3>
                  <label>
        <input
          type="checkbox"
          onChange={handleChange(note)}
        />
        heart
      </label>
                </div>
                <div className="notebookInfo-description">
                  <p>{note.description}</p>
                </div>
              </div>
            </React.Fragment>
          ))}
        </div>
      </section>
    </>
  );
};

export default Notebook;
