import './profile.css'
import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/database";
import {useAuthValue} from './AuthContext'
import { signOut } from 'firebase/auth' 
import { auth } from './firebase'
import Navbar from "./components/Navbar";
import NoteAdd from "./components/NoteAdd";
import Notebook from "./components/Notebook";

const firebaseConfig = {
  apiKey: "AIzaSyBabo7XfrbgD_z4qIsf3KWHnPwwqUBM5xI",
  authDomain: "fir-user-reg-auth.firebaseapp.com",
  projectId: "fir-user-reg-auth",
  storageBucket: "fir-user-reg-auth.appspot.com",
  messagingSenderId: "720211998290",
  appId: "1:720211998290:web:8291fab2697dbaafc2d18b"
}

function Profile() {
  const [noteBookData, setNoteBookData] = useState([]);
  const {currentUser} = useAuthValue()
  const updateNotes = () => {
    auth
      .database()
      .ref("notebook")
      .on("child_added", (snapshot) => {
        let note = {
          email: currentUser?.email,
          public: false,
          id: snapshot.key,
          title: snapshot.val().title,
          description: snapshot.val().description,
        };
        let notebook = noteBookData;
        notebook.push(note);
        setNoteBookData([...noteBookData]);
      });

    auth
      .database()
      .ref("notebook")
      .on("child_removed", (snapshot) => {
        let notebook = noteBookData;
        notebook = noteBookData.filter((note) => note.id !== snapshot.key);
        setNoteBookData(notebook);
      });
  };

  useEffect(() => {
    updateNotes();
  }, []);

  return (
    <div className="app">
      <Navbar />
      <div className="note-section">
        <NoteAdd />
        <Notebook notebook={noteBookData} currUser={currentUser?.email} />
      </div>
      <span onClick={() => signOut(auth)}>Sign Out</span>
    </div>
  );
}

export default Profile

